	try {
		input = JSON.parse(intent).resource.text;
		return {
			input: input,
			intent: findIntention(intent, accuracy)
		}
	}
	catch (e) {
		return e.message;
	}
}

function findIntention(intent, accuracy) {
	try {
		intent = JSON.parse(intent).resource.intentions
		indentifiedIntent = intent.filter(({ score }) => score > Number(accuracy))
		if (indentifiedIntent.length > 0) {
			return indentifiedIntent[0].name;
		}
		return null
	} catch (err) {
		return null
	}
}

