      if (match) {
        let startDay = match[1].padStart(2, '0');
        let endDay = match[4].padStart(2, '0'); // Corrigido para usar match[4]
        const startMonth = getMonthNumber(match[2]);
        const endMonth = getMonthNumber(match[5]); // Corrigido para usar match[5]
        const year = currentDate.getFullYear(); // Mantenha o ano atual
