		let filteredData = data.filter(card => card.numero == finalCartao);
		filteredData = filteredData.map(card => {
			switch (card.funcao) {
				case 'M':
					card.funcao = 'débito e crédito';
					break;
				case 'D':
					card.funcao = 'débito';
					break;
				case 'C':
					card.funcao = 'crédito';
					break;
			}
			return card;
		});

		let cartao = filteredData[0];
		let cartaoBin = cartao.numeroConta.substr(0, 6)
		let cartaoInfo = bin.filter(x => x.binFidelity == cartaoBin)
		cartao.descricao = `${cartaoInfo[0].nomeComercial}`;
		return cartao;

	}
	catch (error) {
		return error.message;
	}
}

