function run(jsonStr1, jsonStr2) {
	try {
		// Inicializa os objetos como vazios caso as strings estejam nulas ou inválidas
		let obj1 = jsonStr1 ? JSON.parse(jsonStr1) : { data: [] };
		let obj2 = jsonStr2 ? JSON.parse(jsonStr2) : { data: [] };

		// Inicializa um array para consolidar os dados dos dois objetos
		let dadosUnificados = [
			...(obj1.data || []), // Se obj1.data for undefined, usa um array vazio
			...(obj2.data || [])  // Se obj2.data for undefined, usa um array vazio
		];

		// Remove duplicatas com base em operacao.numero
		let numerosVistos = new Set();
		dadosUnificados = dadosUnificados.filter(item => {
			let numeroOperacao = item.operacao.numero;
			if (numeroOperacao && !numerosVistos.has(numeroOperacao)) {
				numerosVistos.add(numeroOperacao);
				return true; // Mantém este item
			}
			return false; // Remove duplicatas
		});

		// Retorna o objeto unificado com a contagem de elementos
		return {
			data: dadosUnificados,
			dataLength: dadosUnificados.length
		};
	}
	catch (e) {
		return e.message;
	}
}

