const menu = (dataArray, index) => {
    const itemsPerPage = 9;
    dataArray = JSON.parse(dataArray).data;
    dataArray.sort((a, b) => new Date(b.data) - new Date(a.data));
    let startIndex = (index - 1) * itemsPerPage;
    let endIndex = index * itemsPerPage;
    let paginatedItems = dataArray.slice(startIndex, endIndex);

    let objOpcoes = paginatedItems.map(data => ({
        "id": `${data.operacao.numero}`,
        "title": formatarNomeTransferencia(data.operacao.tipo),
        "description": `${formatarData(data.data)} - R$ ${formatarValor(data.valor)}`
    }));
    if (endIndex < dataArray.length) {
        objOpcoes.push({
            "id": "VerMais",
            "title": "Ver mais",
            "description": "Ver mais comprovantes do período"
        });
    }
    let textMenu = `De qual comprovante você precisa?\n\nÉ só tocar no botão abaixo que te mostro.`;
    if (index > 1) {
        let textMenu = `Aqui estão mais comprovantes:\n\nSe você quiser ver os comprovantes anteriores, basta selecionar no menu anterior.`;
    }


    return {
        "body": {
            "text": textMenu
        },
        "action": {
            "button": "Ver comprovantes",
            "sections": [
                {
                    "rows": objOpcoes
                }
            ]
        }
    };
};

