function run(inputUsuario) {
    const invalidInputMessage = 'input inesperado'
    try {
      if (!inputUsuario) {
        return 'inatividade'
      }
      const regexDictionary = {
        "^\\d{14}$": "Protocolo válido"
      }
      for (let regex in regexDictionary) {
        if (new RegExp(regex, 'i').test(inputUsuario)) {
          return regexDictionary[regex]
        }
      }
      return invalidInputMessage
    } catch (err) {
      return invalidInputMessage
    }
  }

