function run(bodyOcorrencia,bodyProtocolo) {
    try {
        let obj = JSON.parse(bodyOcorrencia).data;
        let objProtocolo = JSON.parse(bodyProtocolo).data;
        let dataAtual = new Date();
        let itemAberto;
        for (let i = 0; i < obj.length; i++) {
            if (obj[i].status.descricao == "ABERTO" || obj[i].status.descricao == "EM ANDAMENTO") {
                itemAberto = obj[i];
                break;
            }
        }
        if (itemAberto) {
            let dataLimite = getAddDoisDiasUteis(itemAberto.dataHoraAbertura,objProtocolo.maiorSlaOcorrencia);
            if (dataLimite < dataAtual) {
                return `Fora do prazo`;
            }
        }
        return `Dentro do prazo`;
    }
    catch (e) {
        return e.message;
    }
}
function getAddDoisDiasUteis(data,maiorSLAOcorrencia) {
    maiorSLAOcorrencia = maiorSLAOcorrencia ? parseInt(maiorSLAOcorrencia) : 4;
    data = new Date(data);
    for (let i = 0; i < maiorSLAOcorrencia;) {
        data.setDate(data.getDate() + 1);
        if (data.getDay() !== 0 && data.getDay() !== 6) {
            i++;
        }
    }
    return data;
}

