function run(bodyConsultarProtocolo, bodyConsultarOcorrencias) {
    try {
        bodyConsultarProtocolo = JSON.parse(bodyConsultarProtocolo).data;
        bodyConsultarOcorrencias = JSON.parse(bodyConsultarOcorrencias).data;
        let msgsProtocolo = "Segue status detalhado do seu protocolo:\n\n";
        msgsProtocolo += bodyConsultarOcorrencias.map((item) => {
            let msgProtocolo = `*Ocorrência*: ${item.motivo.descricao} - ${item.subMotivo.descricao}
*Status*: ${item.status.descricao}
*Aberto em*: ${getDataFormatada(item.dataHoraAbertura)}`;
            if (bodyConsultarProtocolo.statusDescricao == 'ENCERRADO') {
                if (item.status.dataHoraEncerramento != "0001-01-01T00:00:00") {
                    msgProtocolo += `\n*Encerrado em*: ${getDataFormatada(item.status.dataHoraEncerramento)}`;
                }
                if (item.status.motivoResolucao) {
                    msgProtocolo += `\n*Comentario*: ${item.status.motivoResolucao}`;
                }
            } else {
                msgProtocolo += `\n*Retorno previsto*: ${getAddDoisDiasUteis(item.dataHoraAbertura, bodyConsultarProtocolo.maiorSlaOcorrencia)}`;
                 if (item.status.motivoResolucao) {
                    msgProtocolo += `\n*Comentario*: ${item.status.motivoResolucao}`;
                }
            }
            return msgProtocolo;
        }).join("\n\n");

        return msgsProtocolo;
    }
    catch (e) {
        return e.message;
    }
}
function getDataFormatada(data) {
    let dataHoraInclusao = new Date(data);
    let dia = ("0" + dataHoraInclusao.getDate()).slice(-2);
    let mes = ("0" + (dataHoraInclusao.getMonth() + 1)).slice(-2);
    let ano = dataHoraInclusao.getFullYear();
    return `${dia}/${mes}/${ano}`;
};

function getAddDoisDiasUteis(data, maiorSLAOcorrencia) {
    data = new Date(data);
    maiorSLAOcorrencia = maiorSLAOcorrencia ? parseInt(maiorSLAOcorrencia) : 4;
    for (let i = 0; i < maiorSLAOcorrencia;) {
        data.setDate(data.getDate() + 1);
        if (data.getDay() !== 0 && data.getDay() !== 6) {
            i++;
        }
    }
    return getDataFormatada(data);
}

