function run(json) {
    try {
        let data = JSON.parse(json).data;
        let uniqueIdProtocoloStatus = data.reduce((acc, item) => {
            if (!acc.some(i => i.idProtocolo === item.idProtocolo)) {
                acc.push({ idProtocolo: item.idProtocolo, descricaoStatus: item.descricaoStatus });
            }
            return acc;
        }, []);

        let numeroDeProtocolos = uniqueIdProtocoloStatus.length;

        return {
            uniqueIdProtocoloStatus,
            numeroDeProtocolos
        };
    }
    catch (err) {
        return {
            uniqueIdProtocoloStatus: [],
            numeroDeProtocolos: 0,
            error: err.message
        }
    }
};

