function run(bodyContas) {
	try {
		const contas = JSON.parse(bodyContas).data;
		let arrContas = []
		let totalLoops = 0;

		for (let index = 0; index < contas.length; index++) {
			if (totalLoops > 100) break;
			for (let indexB = 0; indexB < contas[index].contas.length; indexB++) {
				if (totalLoops > 100) break;
				const novaConta = contas[index].contas[indexB];
				novaConta.canal = contas[index].canal
				novaConta.shortname = contas[index].shortname;
				novaConta.usuario = contas[index].usuario
				novaConta.codigoSegmento = contas[index].segmento
				arrContas.push(novaConta)
				totalLoops++;
			}
			totalLoops++;
		}
		return {
			quantidadeContas: arrContas.length,
			contas: arrContas
		}
	} catch (err) {
		return {
			quantidadeContas: 0,
			contas: []
		}
	}
}

