function run(bodyEC) {

    const bodyEC = JSON.parse(bodyEC);

    const objEC = [];

    bodyEC.data.forEach(function (itens) {
        objEC.push({
            codigoEc: itens.codigoEc,
            indSitEC: itens.indSitEC,
            nomeRazaoSocial: itens.nomeRazaoSocial,
            cnpj: itens.cnpj,
            tipoPessoas: itens.tipoPessoas,
        })
    })
    return JSON.stringify(objEC);
}

function run(ec) {
    const data = JSON.parse(ec);

    const safrapayClient = data.map((item) => {
        return {
            codigoEc: item.codigoEc,
            indSitEC: item.indSitEC
        }
    })

    let availableSafrapayClients = safrapayClient.filter((client) => client.indSitEC == 'A')[0]

    if (!availableSafrapayClients) {
        availableSafrapayClients = safrapayClient[0]
        if (!availableSafrapayClients) {
            availableSafrapayClients = ""
        }
    }  
    return JSON.stringify(availableSafrapayClients);
}

