function run(valor) {

  valor = valor.replace('.',',')
  let valorFormatado = valor.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })

  if( valorFormatado.indexOf('.') !== -1 && valorFormatado.indexOf(',') !== -1){
    let splitVirgula = valorFormatado.split(',')
    let decimais = splitVirgula[1]+'00'
    return splitVirgula[0]+","+decimais.substring(0,2)
  }

  else if(valorFormatado.indexOf('.') !== -1){
    return valorFormatado+',00'
  }
  else if(valorFormatado.indexOf(',') !== -1){
    let splitVirgula = valorFormatado.split(',');
    let decimais = splitVirgula[1]+"00"
    let milhar = splitVirgula[0].replace(/\D/g, '');
    milhar = milhar.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
    return milhar + ',' + decimais.substring(0,2);
  }else{
    let milhar = valorFormatado.replace(/\D/g, '');
    milhar = milhar.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
    return milhar +",00"
  }
};

