function run(bodyApolice, bodyAlugueis, bodyFaturamentoMensal, bodyFaturamentoAnual,numeroSerie,numeroLogico, consultMaquina) {
	mensagem = ''
	bodyAlugueis = JSON.parse(bodyAlugueis);
	bodyApolice = JSON.parse(bodyApolice);
	consultMaquina = JSON.parse(consultMaquina);
	bodyFaturamentoMensal = JSON.parse(bodyFaturamentoMensal);
	bodyFaturamentoAnual = JSON.parse(bodyFaturamentoAnual);

	const formartValue = (valor) => {
    return  `R$ ${
        parseFloat(valor)
        .toFixed(2)
        .replace(".", ",")
        .replace(/\B(?=(\d{3})+(?!\d))/g, ".")}`;
}
if(consultMaquina.data){
    consultMaquina = consultMaquina.data.filter(consultMaquina => (consultMaquina.numeroSerie === numeroSerie))
      mensagem += `<b>{{nomeTipoEquipamento}}</b>\n<b>N° de série:</b> ${numeroSerie}\n\n`
}
if(bodyAlugueis.data.length > 0){
    bodyAlugueis = bodyAlugueis.data.filter(bodyAlugueis => (bodyAlugueis.numeroSerie === numeroSerie))
      mensagem += `<b>Aluguel contratual:</b> ${formartValue(bodyAlugueis[0].valorTabela)}/ mês \n<b>Último valor pago:</b> ${formartValue(bodyAlugueis[0].valorPago)}\n\n`
}
if(bodyApolice.data){
    bodyApolice = bodyApolice.data.filter(bodyApolice => (bodyApolice.numeroSerie === numeroSerie))
    if(bodyApolice.descricaoStatus === 'Vigente'){
        mensagem += `<b>Seguro:</b> R$9,90/ mês\n\n`
    }
}
if(bodyFaturamentoMensal.data){
    if(bodyFaturamentoMensal.data.length >0){
        bodyFaturamentoMensal = bodyFaturamentoMensal.data.filter(bodyFaturamentoMensal => (bodyFaturamentoMensal.numeroLogico === numeroLogico))
        if(bodyFaturamentoMensal.length >0){
        mensagem += `Valor realizado até agora: \n• <b>Mensal:</b> ${formartValue(bodyFaturamentoMensal[0].vopRealizado)} \n`
        }
    }
}
if(bodyFaturamentoAnual.data){
    if(bodyFaturamentoAnual.data.length >0){
        bodyFaturamentoAnual = bodyFaturamentoAnual.data.filter(bodyFaturamentoAnual => (bodyFaturamentoAnual.numeroLogico === numeroLogico))
        if(bodyFaturamentoAnual.length >0){
        mensagem += `• <b>Anual:</b> ${formartValue(bodyFaturamentoAnual[0].valorVop)} \n`
        }
    }
}
	return mensagem
}

