  "text": "Selecione abaixo a bandeira, para saber onde vai receber as vendas feitas com ela (domicílio bancário).",
  "options": Object.keys(bandeirasFiltradas).map(bandeira => {
    return {"text": bandeirasFiltradas[bandeira]};
  })
};

// Converter o resultado para JSON
const jsonResultado = JSON.stringify(resultJSON, null, 2);
	return jsonResultado
}

