/**
* All input variables needs to be passed as function param;
* Objects received as param needs to be parsed. Ex.: JSON.parse(inputVariable1);
* Objects returned needs to be stringfied. Ex.: JSON.stringify(inputVariable1);
**/
function run(bodyPacote) {


let jsonData = JSON.parse(bodyPacote)
// Extrair valores
let primeiroItem = jsonData.data[0];
let nome = primeiroItem.nome;
let valor = primeiroItem.valor;

// Convertendo o número para uma string
let valorString = valor.toString();

// Substituindo ponto por vírgula
let valorFormatado = valorString.replace('.', ',');

// Formatando como moeda
valorFormatado = valorFormatado.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });

// Verificando se há ponto e vírgula na formatação
if (valorFormatado.indexOf('.') !== -1 && valorFormatado.indexOf(',') !== -1) {
  let splitVirgula = valorFormatado.split(',');
  let decimais = splitVirgula[1] + '00';
  valor = splitVirgula[0] + ',' + decimais.substring(0, 2);
} else if (valorFormatado.indexOf('.') !== -1) {
  valor = valorFormatado + ',00';
} else if (valorFormatado.indexOf(',') !== -1) {
  let splitVirgula = valorFormatado.split(',');
  let decimais = splitVirgula[1] + '00';
  let milhar = splitVirgula[0].replace(/\D/g, '');
  milhar = milhar.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
  valor = milhar + ',' + decimais.substring(0, 2);
} else {
  let milhar = valorFormatado.replace(/\D/g, '');
  milhar = milhar.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
  valor = milhar + ',00';
}


return(`Nome do serviço: ${nome}\nValor: R$${valor}`);

}

