function run(valor, message) {
    try {
        const messageType = JSON.parse(message).type
        if (messageType !== "text/plain") {
            return 'input inesperado'
        }

        if (!/^\d{1,3}(\.\d{3})*,\d{2}$/.test(valor)) {
            return 'input inesperado';
        }

        const valorNumerico = valor.replace(/\./g, '').replace(',', '.');
        const valorFormatado = parseFloat(valorNumerico).toFixed(2).replace('.', ',');
        return 'R$ ' + valorFormatado;
    } catch (err) {
        return err.message
    }
}

